<template>
  <div class=\"relative inline-block text-left\">\n    <slot name=\"trigger\" :toggle=\"toggle\" :isOpen=\"isOpen\" />\n    <div\n      v-if=\"isOpen\"\n      class=\"absolute right-0 z-50 mt-2 w-56 rounded-md border bg-popover shadow-lg\"\n      @click.away=\"close\"\n    >\n      <slot />\n    </div>\n  </div>\n</template>\n\n<script setup lang=\"ts\">\nimport { ref } from 'vue'\n\nconst isOpen = ref(false)\n\nconst toggle = () => {\n  isOpen.value = !isOpen.value\n}\n\nconst close = () => {\n  isOpen.value = false\n}\n\ndefineExpose({\n  toggle,\n  close\n})\n</script>